-- SELECT * FROM user_calendar LEFT JOIN time_dimension on time_dimension.user_calendar_calendar_dimension_ref = user_calendar.calendar_dimension_ref LEFT JOIN calendar_dimension on calendar_dimension.id = user_calendar.calendar_dimension_ref WHERE user_ref = 2 AND user_calendar.calendar_dimension_ref >= 20150101;
SELECT cd.id, cd.day_name, cd.month_name, cd.day, cd.month, cd.year, cd.week, cd.quarter, cd.weekend_flag, uc.event, uc.holiday_flag, td.start_time, td.end_time, td.total_time, c.name AS category, td.note FROM user_calendar uc LEFT JOIN time_dimension td on td.user_calendar_calendar_dimension_ref = uc.calendar_dimension_ref LEFT JOIN calendar_dimension cd on cd.id = uc.calendar_dimension_ref LEFT JOIN category c on c.user_ref = uc.user_ref AND td.category_ref = c.id WHERE uc.user_ref = 1 AND uc.calendar_dimension_ref >= 20150101;